pattern.xpath:
(
    exists(
        marc:datafield[@tag='260']/marc:subfield[@code='c'][
            matches(., '\d{4}-|-\d{4}')
        ]
    )
)
or
(
    exists(
        marc:datafield[@tag='264']/marc:subfield[@code='c'][
            matches(., '\d{4}-|-\d{4}')
