( (substring(marc:leader, 7, 1) = 'a' or substring(marc:leader, 7, 1) = 't') and ( substring(marc:leader, 8, 1) = 'a' or substring(marc:leader, 8, 1) = 'c' or substring(marc:leader, 8, 1) = 'd' or substring(marc:leader, 8, 1) = 'm'
) and ( substring(marc:controlfield[@tag='008'], 30, 1) = '1' ) ) or ( (substring(marc:leader, 7, 1) = 'a' or substring(marc:leader, 7, 1) = 't') and ( substring(marc:leader, 8, 1) = 'a' or substring(marc:leader, 8, 1) = 'c' or substring(marc:leader, 8, 1) = 'd' or substring(marc:leader, 8, 1) = 'm'
) and ( exists(marc:controlfield[@tag='006'][substring(., 13, 1) = '1']) ) )

some $item in $List245bCollectionTerms satisfies marc:datafield[@tag='245']/marc:subfield[@code='b'][
    matches(lower-case(.), $item)
]

some $item in $ListVAgentEAggregators satisfies marc:datafield[starts-with(@tag, '1')]/marc:subfield[@code='e'][
   replace(lower-case(.), '[\p{P}]+$', '') = replace(lower-case($item), '[\p{P}]+$', '')
]

some $item in $ListVTextsCCT satisfies marc:datafield[@tag='240']/marc:subfield[@code='a'][
    replace(., '[\p{P}]+$', '') = replace($item, '[\p{P}]+$', '')
]

